            document.cookie = name + "=" + cval + ";expires="
                + exp.toGMTString();
    },
    clearCookies() {//删除所有cookies
        var cookies = document.cookie.split(";");
        for (var i = 0; i < cookies.length; i++) {
            var exp = new Date();
            exp.setTime(exp.getTime() - 1);
