<style>
    .el-header, .el-footer {
        /*background-color: #B3C0D1;*/
        color: #333;
        text-align: center;
        /*line-height: 60px;*/
    }
    .el-header{
        height: 80px !important;
        background-color: rgb(64, 158, 255);
        line-height: 80px !important;
    }
    .el-header img{
        height: 66px;
        width: 66px;
        position: relative;
        top: 5px;
        border-radius: 4px;
    }
    .el-header h3{
        color: #FFF;
        font-size: 20px;
        position: relative;
        left: 10px;
        display: inline-block;
    }
    .el-header .user-photo{
        height: 50px;
        width: 50px;
        border-radius: 50%;
        margin-top: 10px;
    }

    .el-header .user{
        position: relative;
        top: -15px;
        left: 5px;
        font-size: 14px;
        color: #FFF;
    }
    .el-aside {
        background-color: #f7f7f7;
        color: #333;
        text-align: center;
        height: 100%;
        /*line-height: 200px;*/
    }
    .el-menu--collapse{
        width: auto;
    }
    .el-menu{
        border-right-width: 0px;
    }
    .el-menu-vertical-demo{
        background-color: #FFF;
    }

