                    <el-menu   class="el-menu-vertical-demo"  :collapse="true">
                        <el-submenu :index="(index+1)+''" :key="'fMenu'+index" v-if="rightMenus.indexOf(fMenu.uuid)>-1" v-for="(fMenu,index) in menuStruct">
